mapper.select:
SELECT message_received.status AS status, message_received.received_user_id AS received_user_id, message_received.id AS id, message.id as message_id, message.create_time, message.update_time, message.create_user, message.update_user, message.is_deleted, message.title, message.send_user_id, message.content, message.cover, message.summary, message.editor_type, message.level, message.extra, message_type.message_name AS message_type, message_type.id AS message_type_id, received_user.nickname received_user_nickname, create_user.username AS create_username, update_user.username AS update_username, send_user.nickname AS send_nickname FROM sys_message_received message_received LEFT JOIN sys_user create_user ON create_user.id = message_received.create_user LEFT JOIN sys_user update_user ON update_user.id = message_received.update_user LEFT JOIN sys_user received_user ON message_received.received_user_id = received_user.id LEFT JOIN sys_message message ON message_received.message_id = message.id LEFT JOIN sys_user send_user ON send_user.id = message.send_user_id LEFT JOIN sys_message_type message_type ON message_type.id = message.message_type
message_received.is_deleted = 0
and message.title like CONCAT('%',#{dto.title},'%')
and send_user.nickname like CONCAT('%',#{dto.sendNickname},'%')
and message_type.message_type = #{dto.messageType}
and message.editor_type = #{dto.editorType}
and message.level = #{dto.level}
and message.extra like CONCAT('%',#{dto.extra},'%')
and message_received.status = #{dto.status}

SELECT message.id, message.title, message.cover, message.summary, message.level, message.extra, message.update_time, message_received.id AS message_received_id, message_received.status AS status, message_type.message_type AS message_type FROM sys_message message LEFT JOIN sys_user create_user ON create_user.id = message.create_user LEFT JOIN sys_user update_user ON update_user.id = message.update_user LEFT JOIN sys_message_type message_type ON message_type.id = message.message_type INNER JOIN sys_message_received message_received ON message_received.received_user_id = #{userId} AND message.id = message_received.message_id
message.is_deleted = 0 and message_received.is_deleted = 0
and message_received.status = #{dto.status}
and message_type.message_type = #{dto.messageType}
and message.title like CONCAT('%',#{dto.title},'%')
