mapper.select:
SELECT message.id, message.title, message.cover, message.summary, message.level, message.extra, message.update_time, message_received.id AS message_received_id, message_received.status AS status, message_type.message_type AS message_type FROM sys_message message LEFT JOIN sys_user create_user ON create_user.id = message.create_user LEFT JOIN sys_user update_user ON update_user.id = message.update_user LEFT JOIN sys_message_type message_type ON message_type.id = message.message_type INNER JOIN sys_message_received message_received ON message_received.received_user_id = #{userId} AND message.id = message_received.message_id
message.is_deleted = 0
and message_received.status = #{dto.status}
and message_type.message_type = #{dto.messageType}
and message.title like CONCAT('%',#{dto.title},'%')
